import React, {Component} from 'react';
import instance from '../components/carrecord.js';
import Head from "next/head";
import {Button, Card, Container, Dropdown, Form, Icon, Image, Menu, Message} from "semantic-ui-react";
import web3 from "../components/web3.js"
import Link from 'next/link';
import FixedMenuLayout from "../components/footer";

class ManageAdmin extends Component {
    static async getInitialProps() {
        let accounts = await web3.eth.getAccounts();
        try {
            let owner = await instance.methods.checkIfOwner(accounts[0]).call();
            let admin = await instance.methods.checkIfAdmin(accounts[0]).call();
            return {owner, admin};
        } catch (err) {

        }
    };

    state = {
        loading: false,
        errorMessage: '',
        name: '',
        removeadmin: '',
        address: '',
        accounts: [],
        adminsearch:'',
        isAdmin: false,
        showifadmin: false
    };


    onSubmit = async event => {

        try {
            event.preventDefault();
            this.setState({accounts: await web3.eth.getAccounts()});
            this.setState({loading: true, errorMessage: ''});
            await instance.methods.addAdmin(this.state.address, this.state.name).send({
                from: this.state.accounts[0]
            });

        } catch (err) {
            this.setState({errorMessage: err.message});
        }

        this.setState({loading: false});
        console.log(accounts);
    };


    onSubmitRemove = async event => {

        try {
            event.preventDefault();
            this.setState({accounts: await web3.eth.getAccounts()});
            this.setState({loading: true, errorMessage: ''});
            await instance.methods.removeAdmin(this.state.removeadmin).send({
                from: this.state.accounts[0]
            });

        } catch (err) {
            this.setState({errorMessage: err.message});
        }

        this.setState({loading: false});
        console.log(accounts);
    };

    onSubmitSearch = async event => {

        try {
            event.preventDefault();
            this.setState({accounts: await web3.eth.getAccounts()});
            this.setState({loading: true, errorMessage: ''});
            this.setState({isAdmin: await instance.methods.checkAdmin(this.state.adminsearch).send({
                from: this.state.accounts[0]})});

        } catch (err) {
            this.setState({errorMessage: err.message});
        }

        this.setState({loading: false});
        console.log(accounts);
    };



    handleChangeAddress = event => {
        event.preventDefault();
        this.setState({address: event.target.value});
        this.setState({errorMessage: ''});
    };

    handleChangeName = event => {
        event.preventDefault();
        this.setState({name: event.target.value});
        this.setState({errorMessage: ''});
    };

    handleRemoveName = event => {
        event.preventDefault();
        this.setState({removeadmin: event.target.value});
