import React, {Component} from 'react';
import instance from '../components/carrecord.js';
import Head from "next/head";
import {Button, Card, Container, Dropdown, Form, Image, Menu, Message} from "semantic-ui-react";
import web3 from "../components/web3.js"
import Link from 'next/link';

class ManageAdmin extends Component {
    static async getInitialProps() {
        let accounts = await web3.eth.getAccounts();
        try {
            let owner = await instance.methods.checkIfOwner(accounts[0]).call();
            let admin = await instance.methods.checkIfAdmin(accounts[0]).call();
            return {owner, admin};
        } catch (err) {

        }
    };

    state = {
        loading: false,
        errorMessage: '',
        name: '',
        removeadmin: '',
        address: '',
        accounts: []
    };


    onSubmit = async event => {

        try {
            event.preventDefault();
            this.setState({accounts: await web3.eth.getAccounts()});
            this.setState({loading: true, errorMessage: ''});
            await instance.methods.addAdmin(this.state.address, this.state.name).send({
                from: this.state.accounts[0]
            });

        } catch (err) {
            this.setState({errorMessage: err.message});
        }

        this.setState({loading: false});
        console.log(accounts);
    };


    onSubmitRemove = async event => {

        try {
            event.preventDefault();
            this.setState({accounts: await web3.eth.getAccounts()});
            this.setState({loading: true, errorMessage: ''});
            await instance.methods.removeAdmin(this.state.removeadmin).send({
                from: this.state.accounts[0]
            });

        } catch (err) {
            this.setState({errorMessage: err.message});
        }

        this.setState({loading: false});
        console.log(accounts);
    };


    handleChangeAddress = event => {
        event.preventDefault();
        this.setState({address: event.target.value});
        this.setState({errorMessage: ''});
    };

    handleChangeName = event => {
        event.preventDefault();
        this.setState({name: event.target.value});
        this.setState({errorMessage: ''});
    };

    handleRemoveName = event => {
        event.preventDefault();
        this.setState({removeadmin: event.target.value});
        this.setState({errorMessage: ''});
    };

    render() {

        let formStyle = {
            margin: '20px auto'
        };
        let buttonStyle = {
            margin: '4px'
        };

        let cardStyle = {

            width: '50%',
            margin: '30px auto'

        };

        let labelStyle = {
            margin: '10px'
        };

        {
            return (

                <div>
                    <div>
                        <Menu fixed='top' inverted>
                            <Container>
                                <Link href="/index">
                                    <Menu.Item as='a' header>
                                        <Image size='normal' src='https://img.icons8.com/cotton/50/000000/retro-car.png'
                                               style={{marginLeft: '1.5em'}}/>
                                        <h2>Car Record</h2>
                                    </Menu.Item>
                                </Link>
                                {this.props.owner === true ?
                                    <Menu.Item as='a'><Link href="/ManageAdmin">Manage Admin</Link></Menu.Item> : null}


                                {this.props.admin === true ? <Dropdown item simple text='Manage Cars'>
                                        <Dropdown.Menu>
                                            <Link href="/AddCar">
                                                <Dropdown.Item as='a'>Add Car</Dropdown.Item>
                                            </Link>
                                            <Link href="/ModifyCar">
                                                <Dropdown.Item as='a'>Modify Car</Dropdown.Item>
                                            </Link>
                                            <Link href="/AddEvent">
                                                <Dropdown.Item as='a'>Add Event</Dropdown.Item>
                                            </Link>
                                            }


                                        </Dropdown.Menu>
                                    </Dropdown>
                                    : null}
                            </Container>

                        </Menu>
                    </div>
                    <Head>
                        <link
                            rel="stylesheet"
                            href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.11/semantic.min.css"
                        />
                        <style>{`
        body {
         background: url("https://images.pexels.com/photos/707047/pexels-photo-707047.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=1280&w=1900");
         background-repeat: no-repeat;
         background-position: center; 
        }
      `}</style>
                    </Head>

                    <h1 style={{margin: '20px'}}>Manage Admins</h1>


                    <Card style={cardStyle}>
                        <label style={labelStyle}><h3>Add Admin</h3></label>
                        <Form className={"form-inline"} style={formStyle} onSubmit={this.onSubmit}
                              error={!!this.state.errorMessage}>
                            <Form.Group inline style={{width: '50%', margin: '30px auto',}}>

                                <Form.Field style={{width: '70%', margin: '10px'}}>

                                    <label>Admin Address:</label>
                                    <input placeholder="Address of the Admin"
                                           aria-placeholder={{color: 'blue'}}
                                           style={{width: '100%'}}
                                           value={this.state.address}
                                           onChange={this.handleChangeAddress}
                                           error={!!this.state.errorMessage}
                                    />


                                    <label>Admin Name:</label>
                                    <input placeholder="Name of the Admin"
                                           aria-placeholder={{color: 'blue'}}
                                           style={{width: '100%'}}
                                           value={this.state.name}
                                           onChange={this.handleChangeName}
                                           error={!!this.state.errorMessage}
                                    />
                                </Form.Field>
                                <Form.Field>
                                    <Button onClick={this.onSubmit} color={"blue"}>
                                        Add
                                    </Button>
                                </Form.Field>
                            </Form.Group>
                            <Message style={buttonStyle} error header={"Error!"} content={this.state.errorMessage}/>
                        </Form>
                    </Card>

                    <Card style={cardStyle}>
                        <label style={labelStyle}><h3>Remove Admin</h3></label>
                        <Form className={"form-inline"} style={formStyle} onSubmit={this.onSubmit}
                              error={!!this.state.errorMessage}>
                            <Form.Group inline style={{width: '60%', margin: '50px auto',}}>
                                <label>Admin Name:</label>
                                <Form.Field style={{width: '70%', margin: '10px'}}>
                                    <input placeholder="Name of the Admin"
                                           aria-placeholder={{color: 'blue'}}
                                           style={{width: '100%'}}
                                           value={this.state.removeadmin}
                                           onChange={this.handleRemoveName}
                                           error={!!this.state.errorMessage}
                                    />
                                </Form.Field>
                                <Form.Field>
                                    <Button onClick={this.onSubmitRemove} color={"blue"}>
                                        Remove
                                    </Button>
                                </Form.Field>
                            </Form.Group>
                        </Form>
                    </Card>


                </div>
            );
        }
    }
}

export default ManageAdmin
