import React, {Component} from 'react';
import instance from '../components/carrecord.js';
import NavbarPage from "../components/Navbar";
import Head from "next/head";
import {Button, Form, Card, Message} from "semantic-ui-react";
import web3 from "../components/web3.js"

class ManageAdmin extends Component {

    state = {
        loading: false,
        errorMessage: '',
        name: '',
        address: '',
        accounts: []
    };




    onSubmit = async event => {

        try {
            event.preventDefault();
            this.setState({accounts: await web3.eth.getAccounts()});
            this.setState({loading: true, errorMessage: ''});
            await instance.methods.addAdmin(this.state.address, this.state.name).send({
                from: this.state.accounts[0]
            });

        } catch (err) {
            this.setState({errorMessage: err.message});
        }

        this.setState({loading: false});
        console.log(accounts);
    };


    onSubmitRemove = async event => {

        try {
            event.preventDefault();
            this.setState({accounts: await web3.eth.getAccounts()});
            this.setState({loading: true, errorMessage: ''});
            await instance.methods.removeAdmin(this.state.name).send({
                from: this.state.accounts[0]
            });

        } catch (err) {
            this.setState({errorMessage: err.message});
        }

        this.setState({loading: false});
        console.log(accounts);
    };


    handleChangeAddress = event => {
        event.preventDefault();
        this.setState({address: event.target.value});
        this.setState({errorMessage: ''});
    }

    handleChangeName = event => {
        event.preventDefault();
        this.setState({name: event.target.value});
        this.setState({errorMessage: ''});
    }

    render() {

        let formStyle = {
            width: '100%',
        };
        let buttonStyle = {
            margin: '4px'
        };

        let cardStyle ={

            width: '50%',
            margin: '30px auto'

        };

        {
            return (

                <div>
                    <NavbarPage/>

                    <Head>

                        <link
                            rel="stylesheet"
                            href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.11/semantic.min.css"
                        />
                    </Head>
                    <h1>Manage Admins</h1>
                <Card style={cardStyle}>
                    <label><h3>Add Admin</h3></label>
                    <Form className={"form-inline"} style={formStyle} onSubmit={this.onSubmit}
                          error={!!this.state.errorMessage}>
                        <Form.Group inline style={{width: '50%', margin: '30px auto',}}>

                            <Form.Field style={{width: '70%', margin: '10px'}}>

                                <label>Admin Address</label>
                                <input placeholder="Address of the Admin"
                                       aria-placeholder={{color: 'blue'}}
                                       style={{width: '100%'}}
                                       value={this.state.address}
                                       onChange={this.handleChangeAddress}
                                       error={!!this.state.errorMessage}
                                />


                                <label>Admin Name:</label>
                                <input placeholder="Name of the Admin"
                                       aria-placeholder={{color: 'blue'}}
                                       style={{width: '100%'}}
                                       value={this.state.name}
                                       onChange={this.handleChangeName}
                                       error={!!this.state.errorMessage}
                                />
                            </Form.Field>
                            <Form.Field>
                                <Button onClick={this.onSubmit} color={"blue"}>
                                    Add
                                </Button>
                            </Form.Field>
                        </Form.Group>
                        <Message style={buttonStyle} error header={"Error!"} content={this.state.errorMessage}/>
                    </Form>
                </Card>

                    <Card style={cardStyle}>
                    <Form className={"form-inline"} style={formStyle} onSubmit={this.onSubmit}
                          error={!!this.state.errorMessage}>
                        <Form.Group inline style={{width: '60%', margin: '30px auto',}}>
                            <label>Admin Name</label>
                            <Form.Field style={{width: '70%', margin: '10px'}}>
                                <input placeholder="Name of the Admin"
                                       aria-placeholder={{color: 'blue'}}
                                       style={{width: '100%'}}
                                       value={this.state.address}
                                       onChange={this.handleChangeAddress}
                                       error={!!this.state.errorMessage}
                                />
                            </Form.Field>
                            <Form.Field>
                                <Button onClick={this.onSubmitRemove} color={"blue"}>
                                    Remove
                                </Button>
                            </Form.Field>
                        </Form.Group>
                    </Form>
                    </Card>






                </div>
            );
        }
    }
}

export default ManageAdmin
