import React, {Component} from 'react';
import instance from '../components/carrecord.js';
import NavbarPage from "../components/Navbar";
import Head from "next/head";
import {Button, Form, Icon, Message} from "semantic-ui-react";

class ManageAdmin extends Component {

    state = {
        loading: false,
        errorMessage: '',
        name: '',
        address: ''
    };


    onSubmit = async event => {

        try {
            event.preventDefault();
            const accounts = await web3.eth.getAccounts();
            this.setState({loading: true, errorMessage: ''});
            await this.instance.methods.addAdmin(this.state.address, this.state.name).send({
                from: accounts[0]
            });
        } catch (err) {
            this.setState({errorMessage: err.message});
        }

        this.setState({loading: false});
    };


    render() {
        let formStyle = {
            width: '100%',
        };
        let buttonStyle = {
            margin: '4px'
        };

        {
            return (

                <div>
                    <NavbarPage/>
                    <Head>
                        <link
                            rel="stylesheet"
                            href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.11/semantic.min.css"
                        />
                    </Head>
                    <Form className={"form-inline"} style={formStyle} onSubmit={this.onSubmit}
                          error={!!this.state.errorMessage}>
                        <Form.Group inline style={{width: '50%', margin: '20px auto'}}>
                            <Form.Field style={{width: '50%'}}>
                                <input placeholder="VIN"
                                       aria-placeholder={{color: 'blue'}}
                                       style={{width: '100%'}}
                                       value={this.state.VIN}
                                       onChange={this.handleChange}
                                       error={!!this.state.errorMessage}
                                />
                            </Form.Field>
                            <Form.Field>
                                <Button onClick={this.onSubmit} aria-label="Search" color={"primary"}>
                                    <Icon color='black' name='search'/>
                                </Button>
                            </Form.Field>
                        </Form.Group>
                        <Message style={buttonStyle} error header={"Error!"} content={this.state.errorMessage}/>
                    </Form>


                </div>
            );
        }
    }
}

    export
    default
    ManageAdmin
