import React, {Component} from 'react';
import instance from '../components/carrecord.js';
import Card from "semantic-ui-react/dist/commonjs/views/Card";
import {Button, Form} from 'semantic-ui-react'
import Head from "next/head";
import {Icon} from 'semantic-ui-react'
import { Message } from 'semantic-ui-react'


class Index extends Component {


    state = {
        VIN: '',
        car: {},
        accidentsCount: '',
        accidents: [],
        showaccident: false,
        loading: false,
        errorMessage: ''
    };


    onClick = async event => {

        event.preventDefault();
        const {VIN} = this.state;
        this.setState({showaccident: true});

        this.setState({loading: true, errorMessage: ''});

        try {
            const y = await instance.methods.getAccidentsCount(VIN).call();
            for (let i = 0; i < y; i++) {
                const z = await instance.methods.getAccident(VIN, i).call();
                this.setState({accidents: [...this.state.accidents, z]});

            }


        } catch (err) {
            this.setState({errorMessage: err.message});

        }
        this.setState({loading: false});
    };

    renderAccidents() {
        const x = this.state.VIN;
        const r = this.state.accidents.map(function (accident) {
            return ([
                <Card>
                    <p key={x}>{accident[0].toString()}</p>
                    <p key={x}>{accident[1].toString()}</p>
                    <p key={x}>{accident[2].toString()}</p>
                    <p key={x}>{accident[3].toString()}</p>
                    <p key={x}>{accident[4].toString()}</p>
                </Card>

            ]);
        });
        return r;
    }

    onSubmit = async event => {
        event.preventDefault();
        const {VIN} = this.state;
        this.setState({showaccident: false});
        this.setState({loading: true, errorMessage: ''});

        try {
            const x = await instance.methods.getCar(VIN).call();
            if (x[0] === "") {
                this.setState({errorMessage: 'INVALID CAR'});

            }
            const g = this.state.VIN;
            if(g === ''){
                this.setState({errorMessage: 'Field is empty'});
            }
            console.log(x);
            this.setState({car: x});
            console.log("this.state.car:");
            console.log(this.state.car);
            console.log(this.state.accidents);
            // this.setState({accidentsCount: await instance.methods.getAccidentsCount(VIN).call()})

        } catch (err) {
            this.setState({errorMessage: err.message});
        }
        this.setState({loading: false});
    };


    render() {
        let cardStyle = {
            display: 'block',
            width: '285px',
            height: 'auto',
            margin: '0'
        };

        let buttonStyle = {
            margin: '4px'
        };

        let formStyle = {
            width: '210px'

        };


        console.log(this.state.car);
        console.log('state', this.state.showaccident);


        return (
            <div>

                <h1>Initial</h1>
                <Head>
                    <title>Initial</title>
                    <link
                        rel="stylesheet"
                        href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.11/semantic.min.css"
                    />
                </Head>

                <Form className={"form-inline"} style={formStyle} onSubmit={this.onSubmit}
                      error={!!this.state.errorMessage}>
                    <Form.Group inline>
                        <Form.Field>
                            <input placeholder="Search Car"
                                   value={this.state.VIN}
                                   onChange={event => this.setState({VIN: event.target.value})}
                                   error={!!this.state.errorMessage}
                            />
                        </Form.Field>
                        <Form.Field>
                            <Button onClick={this.onSubmit} aria-label="Search">
                                <Icon color='black' name='search'/>
                            </Button>
                        </Form.Field>
                    </Form.Group>
                    <Message error header={"Error!"} content={this.state.errorMessage}/>
                </Form>

                <Card style={cardStyle}>
                    <Card.Content>
                        <Card.Description variant="h5">BRAND: </Card.Description>
                        <Card.Description>{this.state.car[0]}</Card.Description>
                        <Card.Description variant="h5">YEAR:</Card.Description>
                        <Card.Description>{parseInt(this.state.car[1]) >= 1 ? parseInt(this.state.car[1]) : ''}</Card.Description>
                        <Card.Description variant="h5">COLOR:</Card.Description>
                        <Card.Description>{this.state.car[2]}</Card.Description>
                        <Card.Description variant="h5">LICENSE:</Card.Description>
                        <Card.Description>{this.state.car[3]}</Card.Description>
                        <Button style={buttonStyle} variant="contained" color="primary"
                                disabled={this.state.showaccident === true} onClick={this.onClick}
                                error={!!this.state.errorMessage}>
                            Show Accidents
                        </Button>
                    </Card.Content>
                </Card>
            </div>


        );
    }
}

export default Index;
