import React, {Component} from 'react';
import instance from '../components/carrecord.js';
import Head from "next/head";
import {Button, Container, Dropdown, Form, Image, Menu, Message, Segment, Input} from "semantic-ui-react";
import web3 from "../components/web3.js"
import Link from 'next/link';
import FixedMenuLayout from "../components/footer";
import SimpleSnackbar from "../components/snackbar";
import DatePicker from "../components/calendar";

class ModifyCar extends Component {

    state = {
        VIN: '',
        day: '',
        month: '',
        year: '',
        type: '',
        mileage: '',
        desc: '',
        loading: false,
        errorMessage: '',
        appointmentDate: null
    };

    onSubmit = async event => {


        try {
            event.preventDefault();
            this.setState({accounts: await web3.eth.getAccounts()});
            this.setState({loading: true, errorMessage: ''});
            await instance.methods.addEvent(this.state.VIN, this.state.day, this.state.month, this.state.year, this.state.type, this.state.mileage, this.state.desc).send({
                from: this.state.accounts[0]
            });

        } catch (err) {
            this.setState({errorMessage: err.message});
            if (this.state.day >= 0)
                if (this.state.year >= 1900 && this.state.year <= 2019) {
                    if (this.state.VIN === '' || this.state.day === '' || this.state.month === '' || this.state.year === '' || this.state.type === '' || this.state.mileage || this.state.desc) {
                        this.setState({errorMessage: "Fields can't be empty"});
                    }
                } else this.setState({errorMessage: "Invalid year"});


            this.setState({loading: false});
        }
    };


    handleAddVIN = event => {
        event.preventDefault();
        this.setState({VIN: event.target.value});
        this.setState({errorMessage: ''});
    };


    handleAddDay = event => {
        event.preventDefault();
        this.setState({day: event.target.value});
        this.setState({errorMessage: ''});
    };


    handleAddMonth = event => {
        event.preventDefault();
        this.setState({month: event.target.value});
        this.setState({errorMessage: ''});
    };


    handleAddYear = event => {
        event.preventDefault();
        this.setState({year: event.target.value});
        this.setState({errorMessage: ''});
    };

    handleChange1(e, date) {
        this.setState({
            appointmentDate: date
        });
    }

    handleAddMileage = event => {
        event.preventDefault();
        this.setState({mileage: event.target.value});
        this.setState({errorMessage: ''});
    };

    handleAddType = event => {
        event.preventDefault();
        this.setState({type: event.target.value});
        this.setState({errorMessage: ''});
    };

    handleAddDesc = event => {
        event.preventDefault();
        this.setState({desc: event.target.value});
        this.setState({errorMessage: ''});
    };


    render() {
        let cardStyle = {
            display: 'block',
            width: '38%',
            margin: '200px auto',
            height: '500px',
        };


        let buttonStyle = {
            margin: '4px'
        };

        console.log('date:', this.state.appointmentDate);

        return (

            <div>
                <div>
                    <Menu fixed='top' inverted>
                        <Container>
                            <Link href="/index">
                                <Menu.Item as='a' header>
                                    <Image size='normal' src='https://img.icons8.com/cotton/50/000000/retro-car.png'
                                           style={{marginLeft: '1.5em'}}/>
                                    <h2>Car Record</h2>
                                </Menu.Item>
                            </Link>
                            <Menu.Item as='a'><Link href="/ManageAdmin">Manage Admin</Link></Menu.Item> :


                            <Dropdown item simple text='Manage Cars'>
                                <Dropdown.Menu>
                                    <Link href="/AddCar">
                                        <Dropdown.Item as='a'>Add Car</Dropdown.Item>
                                    </Link>
                                    <Link href="/ModifyCar">
                                        <Dropdown.Item as='a'>Modify Car</Dropdown.Item>
                                    </Link>
                                    <Link href="/AddEvent">
                                        <Dropdown.Item as='a'>Add Event</Dropdown.Item>
                                    </Link>
                                    }


                                </Dropdown.Menu>
                            </Dropdown>

                        </Container>

                    </Menu>
                </div>
                <Head>
                    <link
                        rel="stylesheet"
                        href="//cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.2.11/semantic.min.css"
                    />
                    <style>{`
        body {
         background: url("https://images.pexels.com/photos/707047/pexels-photo-707047.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=1280&w=1900");
         background-repeat: no-repeat;
         background-position: center; 
        }
      `}</style>
                </Head>

                >


                <Segment inverted textAlign='center' style={cardStyle}>
                    <h2 style={{margin: '12px auto'}}> Add Car </h2>
                    <Form  inverted onSubmit={this.onSubmit}
                           style={{margin: '30px auto'}}
                          error={!!this.state.errorMessage}>

                        <Form.Group inline style={{width: '90%', margin: '30px auto',float:'right'}}>

                            <Form.Field required style={{width: '70%', margin: '10px'}}>


                                <Form.Input fluid label="VIN" placeholder="VIN of the car..."
                                            style={{width: '100%'}}
                                            value={this.state.VIN}
                                            onChange={this.handleAddVIN}
                                            error={!!this.state.errorMessage}
                                />


                                <Form.Input fluid label="BRAND:" placeholder="brand of the car"
                                            style={{width: '96.555%'}}
                                            value={this.state.brand}
                                            onChange={this.handleAddBrand}
                                            error={!!this.state.errorMessage}
                                />


                                <Form.Input fluid label="COLOR:" placeholder="color of the car"
                                            style={{width: '96.555%'}}
                                            value={this.state.color}
                                            onChange={this.handleAddColor}
                                            error={!!this.state.errorMessage}
                                />


                                <Form.Input fluid label="LICENSE:" placeholder="License"
                                            style={{width: '96.555%'}}
                                            value={this.state.license}
                                            onChange={this.handleAddLicense}
                                            error={!!this.state.errorMessage}
                                />
                                <br/>
                                <h4>Date:</h4>
                                <Form.Group inline widths="equal" style={{width: '21%' }}>
                                    <Form.Field style={{margin: '20%'}}>
                                        <Form.Input label="day:"
                                                    style={{width:'100px'}}
                                                    value={parseInt(this.state.day)}
                                                    onChange={this.handleAddDay}
                                                    placeholder="Enter year of the car"
                                                    type="number"
                                        />


                                        <Form.Input label="month:"
                                                    style={{width:'100px'}}
                                                    value={parseInt(this.state.year)}
                                                    onChange={this.handleAddMonth}
                                                    placeholder="Enter year of the car"
                                                    type="number"
                                        />


                                        <Form.Input label="year:"
                                                    style={{width:'100px'}}
                                                    value={parseInt(this.state.year)}
                                                    onChange={this.handleAddYear}
                                                    placeholder="Enter year of the car"
                                                    type="number"
                                        />
                                    </Form.Field>


                                </Form.Group>

                            </Form.Field>


                        </Form.Group>

                        <Form.Field>
                            <Button onClick={this.onSubmit} color={"purple"}>
                                Make Changes
                            </Button>
                        </Form.Field>


                        <Message style={buttonStyle} error header={"Error!"} content={this.state.errorMessage}/>
                    </Form>
                </Segment>


                <div>{this.state.loading === true ? <SimpleSnackbar/> : null}</div>

                <FixedMenuLayout/>

            </div>

        );
    }

}

export default ModifyCar
